import React, { useEffect, useState } from 'react';
import JobList from './components/JobList';
import Search from './components/Search';
import { Job } from './entities/Job';
import { JobTitle } from './entities/JobTitle';
import { JobService } from './services/jobs.service';

const App = () => {

  const jobService = new JobService();

  const [allJobTitles, setAllJobTitles] = useState<JobTitle[]>([]);
  const [allJobs, setAllJobs] = useState<Job[]>([])

  useEffect( () => {
    const getJobTitles = async () => {
      const allJobTitles: JobTitle[] = await jobService.getJobTitles();
      setAllJobTitles(allJobTitles);
    }
    getJobTitles();
  }, []);

  const getJobsForJobTitle = async (id: number) => {
    const allJobs: Job[] = await jobService.getJobsForJobTitle(id);
    setAllJobs(allJobs);
  }

  return (
    <div>
      <h1>PandoLogic Home Task</h1>
      <Search allJobTitles={allJobTitles} onSearch={getJobsForJobTitle}></Search>
      <JobList allJobs={allJobs}></JobList>
    </div>
  );
}

export default App;
