import React, { useState } from 'react';
import Select from 'react-select';
import { JobTitle } from '../entities/JobTitle';

interface ISearch {
  allJobTitles: JobTitle[];
  onSearch: Function;
}

const Search = ({allJobTitles, onSearch} : ISearch ) => {
  const [selectedOption, setSelectedOption] = useState(null);
  const [showOptions, setShowOptions] = useState(false);

  const options: any[] = allJobTitles.map( jobTitle => ({
    label: jobTitle.jobTitleName,
    value: jobTitle.jobTitleId,
  }));

  const handleChange = (selectedOption: any) => {
    setSelectedOption(selectedOption);
  };

  const handleInputChange = (value: string) => {
    return value.length > 1 ? setShowOptions(true) : setShowOptions(false);
  };

  const handleClick = () => {
    return selectedOption == null
      ? alert('You need to select a job title!')
      : onSearch(selectedOption['value']);
  };

  return (
    <div>
      <Select
        isClearable={true}
        value={selectedOption}
        menuIsOpen={showOptions}
        options={showOptions ? options : []}
        placeholder="Enter a job title"
        onChange={handleChange}
        onInputChange={handleInputChange}
      />
      <button onClick={handleClick}>Search</button>
    </div>
  );
}

export default Search;
