import { Job } from "../entities/Job";
import { JobTitle } from "../entities/JobTitle";

export class JobService {

    readonly API_URL: string = 'https://localhost:5001/api/jobtitles';
    allJobs: Job[] = [];
    allJobTitles: JobTitle[] = [];

    public getJobTitles = async () => {
        const response = await fetch(this.API_URL);
        const data = await response.json();
        this.allJobTitles = JSON.parse(data);
        console.log(this.allJobTitles);
    }

    public getJobsForJobTitle = async (jobTitleId: number) => {
        const response = await fetch(`${this.API_URL}/${jobTitleId}/jobs`);
        const data = await response.json();
        this.allJobs = JSON.parse(data);
        console.log(this.allJobs);
    }

}
