import React, { useState } from 'react';
import Select from 'react-select';

interface ISearch {
  options: any[];
  onSearch: Function;
}

const Search = ({options, onSearch} : ISearch ) => {
  const [selectedOption, setSelectedOption] = useState(null);
  const [showOptions, setShowOptions] = useState(false);

  const handleChange = (selectedOption: any) => {
    setSelectedOption(selectedOption);
  };

  const handleInputChange = (value: string) => {
    return value.length > 1 ? setShowOptions(true) : setShowOptions(false);
  };

  const handleClick = () => {
    if (selectedOption == null) {
      return alert('You need to select a job title!');
    }
    onSearch(selectedOption['value']);
  };

  return (
    <div className="search-section">
      <Select
        className="search"
        isClearable={true}
        value={selectedOption}
        menuIsOpen={showOptions}
        options={showOptions ? options : []}
        placeholder="Enter a job title"
        onChange={handleChange}
        onInputChange={handleInputChange}
        components={{
          DropdownIndicator: () => null,
          IndicatorSeparator: () => null,
        }}
        theme={(theme) => ({
          ...theme,
          borderRadius: 0,
        })}
      />
      <button onClick={handleClick} className="btn-search">
        Search
      </button>
    </div>
  );
}

export default Search;
